#!/bin/bash
whois $1 | awk '/^(Registrant|Admin|Tech)/{s=$1}/ Name:/{print s" Name,"substr($0,index($0,$3))}/ Organization:/{print s" Organization,"substr($0,index($0,$3))}/ Street:/{print s" Street,"substr($0,index($0,$3))" "}/ City:/{print s" City,"substr($0,index($0,$3))}/ State\/Province:/{print s" State/Province,"substr($0,index($0,$3))}/ Postal Code:/{print s" Postal Code,"substr($0,index($0,$4))}/ Country:/{print s" Country,"substr($0,index($0,$3))}/ Phone:/{print s" Phone,"substr($0,index($0,$3))}/ Phone Ext:/{print s" Phone Ext:,"}/ Fax:/{print s" Fax,"substr($0,index($0,$3))}/ Fax Ext:/{print s" Fax Ext:,"}/ Email:/{print s" Email,"substr($0,index($0,$3))}' > $1.csv
